mapper.update:
UPDATE tb_settle_account_flow
SET settlement_record_id = #{settlementRecordId}
WHERE id = #{id}

UPDATE tb_settle_account_flow
SET settle_audit_record_id = #{settleAuditRecordId}
WHERE id = #{id}

UPDATE tb_settle_account_flow
SET settlement_record_id = #{settlementRecordId}
WHERE settle_audit_record_id = #{settleAuditRecordId}

UPDATE tb_settle_account_flow
SET settle_audit_record_id = #{settleAuditRecordId}
WHERE app_id = 'hsy'
AND settle_date = #{settleDate}
AND account_id = #{accountId}
AND type = 1

mapper.select:
SELECT account_user_type, account_id, count(id) as count, SUM(income_amount) as amount
FROM tb_settle_account_flow
WHERE app_id = 'hsy'
AND settle_date = #{settleDate}
AND type = 1
GROUP BY account_user_type, account_id;
