mapper.select:
SELECT * FROM tb_split_account_record WHERE receipt_money_account_id = #{accountId} AND split_amount > 0.00

    AND order_no = #{orderNo}


    AND business_type = #{businessType}


    AND create_time BETWEEN #{beginDate} AND #{endDate}

ORDER BY id DESC LIMIT #{firstIndex},#{pageSize}

SELECT sum(split_amount) as splitamount FROM tb_split_account_record
WHERE receipt_money_account_id = #{accountId}
AND split_amount > 0.00

    AND order_no = #{orderNo}


    AND business_type = #{businessType}


    AND create_time BETWEEN #{beginDate} AND #{endDate}

SELECT count(*) FROM tb_split_account_record WHERE receipt_money_account_id = #{accountId} AND split_amount > 0.00

    AND order_no = #{orderNo}


    AND business_type = #{businessType}


    AND create_time BETWEEN #{beginDate} AND #{endDate}

SELECT
    a.business_type,
    sum(a.split_amount) AS splitAmount,
    DATE_FORMAT(a.split_date, '%Y-%m-%d') AS splitDate,
    b.proxy_name,
    b.mark_code
FROM
    tb_split_account_record a
LEFT JOIN tb_dealer b ON b.account_id = a.receipt_money_account_id
WHERE
    a.receipt_money_account_id = #{accountId}
AND split_amount > 0.00

AND a.split_date  >=  #{startTime}



AND a.split_date  <=  #{endTime}



AND a.business_type = #{businessType}

ORDER BY a.split_date DESC LIMIT #{offset},#{pageSize}

SELECT
    COUNT(0)
FROM
    tb_split_account_record a
LEFT JOIN tb_dealer b ON b.account_id = a.receipt_money_account_id
WHERE
    a.receipt_money_account_id = #{accountId}
AND split_amount > 0.00

AND a.split_date  >=  #{startTime}



AND a.split_date  <=  #{endTime}



AND a.business_type = #{businessType}

SELECT
a.*,c.settle_type
FROM
tb_split_account_record a
LEFT JOIN tb_dealer b ON b.account_id = a.receipt_money_account_id
LEFT JOIN tb_order c ON c.order_no = a.order_no
WHERE
a.receipt_money_account_id = #{accountId}

AND split_amount > 0.00


    AND a.split_date  >=  #{startTime}



    AND a.split_date  <=  #{endTime}



    AND a.business_type = #{businessType}


    AND a.order_no = #{orderNo}

ORDER BY a.split_date DESC LIMIT #{offset},#{pageSize}

SELECT
COUNT(a.id)
FROM
tb_split_account_record a
LEFT JOIN tb_dealer b ON b.account_id = a.receipt_money_account_id
WHERE
a.receipt_money_account_id = #{accountId}
AND split_amount > 0.00

    AND a.split_date  >=  #{startTime}



    AND a.split_date  <=  #{endTime}



    AND a.business_type = #{businessType}


    AND a.order_no = #{orderNo}
