SELECT a.pay_order_id, a.business_order_no, a.order_no, a.trade_amount, a.real_pay_amount, a.trade_type, a.payer, a.payee, a.pay_account, a.pay_account_type, a.pay_type, a.pay_success_time, a.poundage, a.pay_rate, a.settle_status, a.settle_time, a.success_settle_time, a.pay_channel_sign, a.remark, a. STATUS, a.create_time, b.merchant_name, b.mark_code, a.sn, a.app_id, (select proxy_name from tb_dealer where id=b.first_dealer_id) as proxyName, (select proxy_name from tb_dealer where id=b.second_dealer_id) as proxyName1, x.SHORTNAME AS merchantName, x.GLOBALID AS markCode, (select proxy_name from tb_dealer where id=b.oem_id) as branchCompany, b.oem_id FROM tb_order a LEFT JOIN tb_merchant_info b ON b.account_id=a.payee LEFT JOIN app_au_user z ON z.ACCOUNTID=a.payee LEFT JOIN app_biz_shop_user_role y ON y.UID=z.ID AND y.ROLE=1 AND y.TYPE=1 LEFT JOIN app_biz_shop x ON x.ID=y.SID WHERE a.trade_type = 1
AND a.create_time >= #{startTime}
AND a.create_time <= #{endTime}
AND a.order_no LIKE CONCAT('%',#{orderNo})
AND b.merchant_name LIKE CONCAT('%',#{merchantName},'%')
AND x.SHORTNAME LIKE CONCAT('%',#{shortName},'%')
AND b.first_dealer_id in (select id from tb_dealer where proxy_name LIKE CONCAT('%',#{proxyName},'%'))
AND b.second_dealer_id in (select id from tb_dealer where proxy_name LIKE CONCAT('%',#{proxyName1},'%'))
AND b.oem_id in (select id from tb_dealer where proxy_name LIKE CONCAT('%',#{branchCompany},'%'))
AND a.business_order_no = #{businessOrderNo}
AND a.sn = #{sn}
AND b.mark_code = #{markCode}
AND x.GLOBALID = #{globalId}
AND a.app_id = #{appId}
AND a.status=#{status}
AND a.pay_type LIKE CONCAT('%',#{payType},'%')
AND a.real_pay_amount >= #{lessTotalFee}
AND a.real_pay_amount <= #{moreTotalFee}
AND a.settle_status = #{settleStatus}
AND a.pay_channel_sign = #{payChannelSign}
ORDER BY a.create_time DESC limit #{offset}, #{size}

