SELECT
a.pay_order_id,a.order_no,a.trade_amount,a.real_pay_amount,a.trade_type,a.payer,a.payee,a.pay_account,
a.pay_account_type,a.pay_type,a.pay_success_time,a.poundage,a.pay_rate,a.settle_status,a.settle_time,
a.success_settle_time,a.pay_channel_sign,a.remark,a. STATUS,a.create_time,b.merchant_name,a.sn,a.app_id,
(SELECT c.proxy_name FROM tb_dealer c WHERE c.id = b.first_dealer_id) AS proxyName,
(SELECT c.proxy_name FROM tb_dealer c WHERE c.id = b.second_dealer_id) AS proxyName1
FROM
tb_order a
LEFT JOIN tb_merchant_info b
ON a.payee=b.account_id
WHERE a.trade_type=1

    AND a.create_time  >=  #{startTime}


    AND a.create_time  <=  #{endTime}


    AND a.order_no LIKE CONCAT('%',#{orderNo})


    AND b.merchant_name LIKE CONCAT('%',#{merchantName},'%')


    AND a.status=#{status}


    AND a.pay_type =  #{payType}


    AND a.real_pay_amount  >=  #{lessTotalFee}


    AND a.real_pay_amount  <=  #{moreTotalFee}


    AND a.settle_status = #{settleStatus}

ORDER BY a.create_time DESC
limit #{offset}, #{size}

