mapper.select:
SELECT COUNT(a.id) FROM tb_account_flow a
LEFT JOIN tb_account b ON a.account_id=b.id
WHERE b.id=#{id}

    AND a.create_time  >=  #{startTime}


    AND a.create_time  <=  #{endTime}

SELECT a.* FROM tb_account_flow a
LEFT JOIN tb_account b ON a.account_id=b.id
WHERE b.id=#{id}

    AND a.create_time  >=  #{startTime}


    AND a.create_time  <=  #{endTime}

ORDER BY create_time DESC
limit #{offset}, #{pageSize}

SELECT a.* FROM tb_account_flow a
LEFT JOIN tb_account b ON a.account_id=b.id
WHERE b.id in (SELECT DISTINCT account_id
FROM `tb_basic_channel`)

    AND a.create_time  >=  #{startTime}


    AND a.create_time  <=  #{endTime}

ORDER BY create_time DESC
limit #{offset}, #{pageSize}

