mapper.select:
SELECT * FROM tb_daily_profit_detail
WHERE shall_money_type = 5

    and dealer_name = #{dealerName}


    and create_time BETWEEN #{beginProfitDate} AND #{endProfitDate}

order by create_time DESC  LIMIT #{index},#{pageSize}

