AND a.mark_code = #{markCode}



AND a.merchant_name = #{merchantName}



AND a.first_dealer_id in (select id from tb_dealer where proxy_name LIKE CONCAT('%',#{proxyName},'%'))



AND a.second_dealer_id in (select id from tb_dealer where proxy_name LIKE CONCAT('%',#{proxyName1},'%'))



AND a.mobile = #{mobile}

SELECT COUNT(*) FROM tb_merchant_info a WHERE a.second_dealer_id = #{dealerId}

AND a.mark_code = #{markCode}



AND a.merchant_name = #{merchantName}



AND a.first_dealer_id in (select id from tb_dealer where proxy_name LIKE CONCAT('%',#{proxyName},'%'))



AND a.second_dealer_id in (select id from tb_dealer where proxy_name LIKE CONCAT('%',#{proxyName1},'%'))



AND a.mobile = #{mobile}

SELECT a.mark_code, a.proxy_name, b.total_amount, b.due_settle_amount, b.available, b.id FROM tb_dealer a LEFT JOIN tb_account b ON b.id=a.account_id WHERE a.oem_type=1
AND a.mark_code = #{markCode}
AND a.proxy_name = #{proxyName}
ORDER BY a.`create_time` DESC LIMIT #{offset},#{pageSize}

