mapper.select:
SELECT COUNT(1) FROM tb_dealer as d

    d.level = 2 and d.first_level_dealer_id=#{dealerId}

        AND d.mobile =#{mobile}


        AND d.`proxy_name` LIKE "%"#{name}"%"


        AND d.mark_code = #{markCode}


        AND (d.belong_province_code = #{districtCode} OR d.belong_city_code=#{districtCode})


        AND d.id IN (select distinct(dealer_id) from tb_dealer_channel_rate where product_id in (select id from tb_product where type=#{sysType}))

SELECT d.id, d.proxy_name, d.mark_code, d.level, d.create_time, d.bank_account_name, d.belong_province_code, d.belong_province_name, d.belong_city_code, d.belong_city_name, ( SELECT DISTINCT (product_id) FROM tb_dealer_channel_rate WHERE dealer_id = d.id AND product_id IN ( SELECT id FROM tb_product WHERE type = 'hss' ) ) AS hss_product_id, ( SELECT DISTINCT (product_id) FROM tb_dealer_channel_rate WHERE dealer_id = d.id AND product_id IN ( SELECT id FROM tb_product WHERE type = 'hsy' ) ) AS hsy_product_id, d.mobile FROM tb_dealer as d
d.level = 2 and d.first_level_dealer_id=#{dealerId}
AND d.mobile =#{mobile}
AND d.`proxy_name` LIKE "%"#{name}"%"
AND d.mark_code = #{markCode}
AND (d.belong_province_code = #{districtCode} OR d.belong_city_code=#{districtCode})
AND d.id IN (select distinct(dealer_id) from tb_dealer_channel_rate where product_id in (select id from tb_product where type=#{sysType}))
ORDER BY d.create_time DESC LIMIT #{offset},#{count}

SELECT d.id, d.mobile, d.proxy_name, (SELECT DISTINCT (product_id) FROM tb_dealer_channel_rate WHERE dealer_id = d.id AND product_id IN ( SELECT id FROM tb_product WHERE type = #{sysType})) as isDistribute FROM tb_dealer as d WHERE d.level = 2
AND (d.mobile LIKE "%"#{condition}"%" OR d.proxy_name LIKE "%"#{condition}"%")
AND d.first_level_dealer_id = #{dealerId}
