mapper.select:
select sum(split_amount) as splitamount from tb_split_account_record where receipt_money_account_id=#{accountid}
and split_date >='${startTime}'
and split_date < '${endTime}'

select sum(o.trade_amount) as tradeamount from tb_order o inner join tb_merchant_info m on o.payee=m.account_id where o.trade_type=1 and o.status=4 and m.dealer_id=#{dealerid}
and o.pay_success_time >='${startTime}'
and o.pay_success_time < '${endTime}'

select sum(o.trade_amount) as tradeamount from tb_order o inner join app_au_user m on o.payee=m.ACCOUNTID where o.trade_type=1 and o.status=4 and m.DEALERID=#{dealerid}
and o.pay_success_time >= '${startTime}'
and o.pay_success_time < '${endTime}'

select sum(o.trade_amount) as tradeamount from tb_order o inner join tb_merchant_info m on o.payee=m.account_id inner join tb_dealer d on m.dealer_id=d.id where o.trade_type=1 and o.status=4 and d.first_level_dealer_id=#{dealerid}
and o.pay_success_time >= '${startTime}'
and o.pay_success_time < '${endTime}'

select sum(o.trade_amount) as tradeamount from tb_order o inner join app_au_user m on o.payee=m.ACCOUNTID inner join tb_dealer d on m.DEALERID=d.id where o.trade_type=1 and o.status=4 and d.first_level_dealer_id=#{dealerid}
and o.pay_success_time >= '${startTime}'
and o.pay_success_time < '${endTime}'

select count(1) as mCount from tb_merchant_info where dealer_id=#{dealerid}
and create_time >= '${startTime}'
and create_time < '${endTime}'

SELECT COUNT(1) as mCount FROM app_biz_shop a LEFT JOIN app_biz_shop_user_role b ON a.ID = b.SID LEFT JOIN app_au_user c ON c.ID = b.UID WHERE b.ROLE = 1 AND c.DEALERID=#{dealerid}
and a.CREATETIME >= '${startTime}'
and a.CREATETIME < '${endTime}'

select count(1) as mCount from tb_merchant_info m inner join tb_dealer d on m.dealer_id=d.id where d.first_level_dealer_id=#{dealerid}
and m.create_time >= '${startTime}'
and m.create_time < '${endTime}'

select count(1) as mCount from tb_merchant_info where dealer_id=#{dealerid}
and checked_time >= '${startTime}'
and checked_time < '${endTime}'

select count(1) as mCount from tb_merchant_info m inner join tb_dealer d on m.dealer_id=d.id where d.first_level_dealer_id=#{dealerid}
and m.checked_time >= '${startTime}'
and m.checked_time < '${endTime}'

SELECT COUNT(1) as mCount FROM app_biz_shop a LEFT JOIN app_biz_shop_user_role b ON a.ID = b.SID LEFT JOIN app_au_user c ON c.ID = b.UID WHERE b.ROLE = 1 AND c.DEALERID=#{dealerid}
and a.AUDITTIME >= '${startTime}'
and a.AUDITTIME < '${endTime}'
