SELECT COUNT(a.ID) FROM app_biz_shop a
LEFT JOIN app_biz_shop_user_role b ON a.ID=b.SID
LEFT JOIN app_au_user c ON c.ID=b.UID
where b.ROLE=1

    AND a.GLOBALID=#{globalID}


    AND a.SHORTNAME=#{shortName}

SELECT COUNT(a.ID) FROM app_biz_shop a
LEFT JOIN app_biz_shop_user_role b ON a.ID=b.SID
LEFT JOIN app_au_user c ON c.ID=b.UID
where b.ROLE=1 AND a.`STATUS`=2

    AND a.GLOBALID=#{globalID}


    AND a.SHORTNAME=#{shortName}

SELECT a.*, c.CELLPHONE,c.DEALERID, ( SELECT proxy_name FROM tb_dealer WHERE id = c.DEALERID ) as proxyNames FROM app_biz_shop a LEFT JOIN app_biz_shop_user_role b ON a.ID = b.SID LEFT JOIN app_au_user c ON c.ID = b.UID WHERE b.ROLE = 1 AND b.TYPE=1
AND a.GLOBALID=#{globalID}
AND a.SHORTNAME=#{shortName}
ORDER BY a.CREATETIME DESC LIMIT #{offset},#{pageSize}

SELECT a.*,c.CELLPHONE FROM app_biz_shop a
LEFT JOIN app_biz_shop_user_role b ON a.ID=b.SID
LEFT JOIN app_au_user c ON c.ID=b.UID
where b.ROLE=1 AND a.`STATUS`=2

    AND a.GLOBALID=#{globalID}


    AND a.SHORTNAME=#{shortName}

ORDER BY
CREATETIME DESC
LIMIT #{offset},#{pageSize}

SELECT
    a.*, c.CELLPHONE,c.DEALERID,
    (SELECT proxy_name FROM tb_dealer WHERE id = c.DEALERID) as proxyName
FROM
  app_biz_shop a
LEFT JOIN app_biz_shop_user_role b ON a.ID = b.SID
LEFT JOIN app_au_user c ON c.ID = b.UID
WHERE
  b.ROLE = 1 AND c.DEALERID in (SELECT id FROM tb_dealer WHERE first_level_dealer_id=#{dealerId} OR id=#{dealerId})

AND a.GLOBALID=#{globalID}



AND a.SHORTNAME=#{shortName}







AND a.CREATETIME  >=  #{startTime}



AND a.CREATETIME  <=  #{endTime}



AND c.CELLPHONE = #{cellphone}



AND a.INDUSTRYCODE = #{industryCode}

ORDER BY
a.CREATETIME DESC
LIMIT #{offset},#{pageSize}

SELECT a.*, c.CELLPHONE,c.DEALERID, (SELECT proxy_name FROM tb_dealer WHERE id = c.DEALERID) as proxyName FROM app_biz_shop a LEFT JOIN app_biz_shop_user_role b ON a.ID = b.SID LEFT JOIN app_au_user c ON c.ID = b.UID WHERE b.ROLE = 1 AND c.DEALERID=#{dealerId}
AND a.GLOBALID=#{globalID}
AND a.SHORTNAME=#{shortName}
ORDER BY a.CREATETIME DESC LIMIT #{offset},#{pageSize}

SELECT COUNT(*) FROM app_biz_shop a LEFT JOIN app_biz_shop_user_role b ON a.ID = b.SID LEFT JOIN app_au_user c ON c.ID = b.UID WHERE b.ROLE = 1 AND c.DEALERID in (SELECT id FROM tb_dealer WHERE first_level_dealer_id=#{dealerId} OR id=#{dealerId})
AND a.GLOBALID=#{globalID}
AND a.SHORTNAME=#{shortName}
ORDER BY a.CREATETIME DESC

SELECT COUNT(*) FROM app_biz_shop a LEFT JOIN app_biz_shop_user_role b ON a.ID = b.SID LEFT JOIN app_au_user c ON c.ID = b.UID WHERE b.ROLE = 1 AND c.DEALERID=#{dealerId}
AND a.GLOBALID=#{globalID}
AND a.SHORTNAME=#{shortName}
ORDER BY a.CREATETIME DESC
