mapper.select:
SELECT
   COUNT(*)
FROM
tb_merchant_info a
LEFT JOIN tb_dealer b ON a.dealer_id = b.id
WHERE
1 = 1

AND a.merchant_name LIKE CONCAT('%',#{merchantName},'%')



AND a.mobile LIKE CONCAT('%',#{mobile},'%')



AND a.status in (#{status},6)



AND a.first_dealer_id in (select id from tb_dealer where proxy_name LIKE CONCAT('%',#{proxyName},'%'))


AND a.second_dealer_id in (select id from tb_dealer where proxy_name LIKE CONCAT('%',#{proxyName1},'%'))


AND a.oem_id in (select id from tb_dealer where proxy_name LIKE CONCAT('%',#{branchCompany},'%'))


AND a.create_time  >=  #{startTime}


AND a.create_time  <=  #{endTime}



AND a.authentication_time  >=  #{startTime1}



AND a.authentication_time  <=  #{endTime1}



AND a.checked_time  >=  #{startTime2}


AND a.checked_time  <=  #{endTime2}


AND a.mark_code LIKE CONCAT('%',#{markCode},'%')

SELECT
COUNT(*)
FROM
tb_merchant_info a
LEFT JOIN tb_dealer b ON a.dealer_id = b.id
WHERE
1 = 1

    AND a.merchant_name LIKE CONCAT('%',#{merchantName},'%')



    AND a.mobile LIKE CONCAT('%',#{mobile},'%')



    AND a.status = #{status}



    AND a.first_dealer_id in (select id from tb_dealer where proxy_name LIKE CONCAT('%',#{proxyName},'%'))


    AND a.second_dealer_id in (select id from tb_dealer where proxy_name LIKE CONCAT('%',#{proxyName1},'%'))


    AND a.create_time  >=  #{startTime}


    AND a.create_time  <=  #{endTime}



    AND a.authentication_time  >=  #{startTime1}



    AND a.authentication_time  <=  #{endTime1}



    AND a.checked_time  >=  #{startTime2}


    AND a.checked_time  <=  #{endTime2}


    AND a.mark_code LIKE CONCAT('%',#{markCode},'%')

SELECT
COUNT(*)
FROM
tb_merchant_info
WHERE 1=1

    AND merchant_name LIKE CONCAT('%',#{merchantName},'%')



    AND status=#{status}


    AND create_time  >=  #{startTime}


    AND create_time  <=  #{endTime}



    AND authentication_time  >=  #{startTime1}



    AND authentication_time  <=  #{endTime1}



    AND checked_time  >=  #{startTime2}


    AND checked_time  <=  #{endTime2}

SELECT a.mark_code, a.id, a.source, a.merchant_name, a.mobile, a. STATUS, a.dealer_id, a.create_time, a.authentication_time, a.checked_time, (select proxy_name from tb_dealer where id=a.first_dealer_id) as proxyName, (select proxy_name from tb_dealer where id=a.second_dealer_id) as proxyName1, (select proxy_name from tb_dealer where id=a.oem_id) as branchCompany, a.oem_id FROM tb_merchant_info a LEFT JOIN tb_dealer b ON a.dealer_id = b.id WHERE 1 = 1
AND a.merchant_name LIKE CONCAT('%',#{merchantName},'%')
AND a.mobile LIKE CONCAT('%',#{mobile},'%')
AND a.status in (#{status},6)
AND a.first_dealer_id in (select id from tb_dealer where proxy_name LIKE CONCAT('%',#{proxyName},'%'))
AND a.second_dealer_id in (select id from tb_dealer where proxy_name LIKE CONCAT('%',#{proxyName1},'%'))
AND a.oem_id in (select id from tb_dealer where proxy_name LIKE CONCAT('%',#{branchCompany},'%'))
AND a.create_time >= #{startTime}
AND a.create_time <= #{endTime}
AND a.authentication_time >= #{startTime1}
AND a.authentication_time <= #{endTime1}
AND a.checked_time >= #{startTime2}
AND a.checked_time <= #{endTime2}
AND a.mark_code LIKE CONCAT('%',#{markCode},'%')
ORDER BY create_time DESC LIMIT #{offset},#{pageSize}

SELECT a.mark_code, a.id, a.source, a.merchant_name, a.mobile, a. STATUS, a.dealer_id, a.create_time, a.authentication_time, a.checked_time, (select proxy_name from tb_dealer where id=a.first_dealer_id) as proxyName, (select proxy_name from tb_dealer where id=a.second_dealer_id) as proxyName1 FROM tb_merchant_info a LEFT JOIN tb_dealer b ON a.dealer_id = b.id WHERE 1 = 1
AND a.merchant_name LIKE CONCAT('%',#{merchantName},'%')
AND a.mobile LIKE CONCAT('%',#{mobile},'%')
AND a.status = #{status}
AND a.first_dealer_id in (select id from tb_dealer where proxy_name LIKE CONCAT('%',#{proxyName},'%'))
AND a.second_dealer_id in (select id from tb_dealer where proxy_name LIKE CONCAT('%',#{proxyName1},'%'))
AND a.create_time >= #{startTime}
AND a.create_time <= #{endTime}
AND a.authentication_time >= #{startTime1}
AND a.authentication_time <= #{endTime1}
AND a.checked_time >= #{startTime2}
AND a.checked_time <= #{endTime2}
AND a.mark_code LIKE CONCAT('%',#{markCode},'%')
ORDER BY create_time DESC LIMIT #{offset},#{pageSize}

SELECT a.mark_code, a.id, a.source, a.merchant_name, a.mobile, a. STATUS, a.dealer_id, a.create_time, a.authentication_time, a.checked_time, (select proxy_name from tb_dealer where id=a.first_dealer_id) as proxyName, (select proxy_name from tb_dealer where id=a.second_dealer_id) as proxyName1 FROM tb_merchant_info a LEFT JOIN tb_dealer b ON a.dealer_id = b.id WHERE 1 = 1
AND a.merchant_name LIKE CONCAT('%',#{merchantName},'%')
AND a.mobile LIKE CONCAT('%',#{mobile},'%')
AND a.status = #{status}
AND a.first_dealer_id in (select id from tb_dealer where proxy_name LIKE CONCAT('%',#{proxyName},'%'))
AND a.second_dealer_id in (select id from tb_dealer where proxy_name LIKE CONCAT('%',#{proxyName1},'%'))
AND a.create_time >= #{startTime}
AND a.create_time <= #{endTime}
AND a.authentication_time >= #{startTime1}
AND a.authentication_time <= #{endTime1}
AND a.checked_time >= #{startTime2}
AND a.checked_time <= #{endTime2}
AND a.mark_code LIKE CONCAT('%',#{markCode},'%')
ORDER BY create_time DESC

SELECT a.mark_code, a.id, a.source, a.merchant_name, a.mobile, a. STATUS, a.dealer_id, a.create_time, a.authentication_time, a.checked_time, (select proxy_name from tb_dealer where id=a.first_dealer_id) as proxyName, (select proxy_name from tb_dealer where id=a.second_dealer_id) as proxyName1 FROM tb_merchant_info a LEFT JOIN tb_dealer b ON a.dealer_id = b.id WHERE 1 = 1
AND a.merchant_name LIKE CONCAT('%',#{merchantName},'%')
AND a.mobile LIKE CONCAT('%',#{mobile},'%')
AND a.status in (#{status},6)
AND a.first_dealer_id in (select id from tb_dealer where proxy_name LIKE CONCAT('%',#{proxyName},'%'))
AND a.second_dealer_id in (select id from tb_dealer where proxy_name LIKE CONCAT('%',#{proxyName1},'%'))
AND a.create_time >= #{startTime}
AND a.create_time <= #{endTime}
AND a.authentication_time >= #{startTime1}
AND a.authentication_time <= #{endTime1}
AND a.checked_time >= #{startTime2}
AND a.checked_time <= #{endTime2}
AND a.mark_code LIKE CONCAT('%',#{markCode},'%')
ORDER BY create_time DESC

SELECT
mark_code,
id,
merchant_name,
STATUS,
dealer_id,
create_time,
authentication_time,
checked_time,
(select d.proxy_name from tb_dealer as d where d.id=first_dealer_id) as proxy_name,
(select d.proxy_name from tb_dealer as d where d.id=second_dealer_id) as proxy_name1
FROM
tb_merchant_info
WHERE 1=1

    AND merchant_name LIKE CONCAT('%',#{merchantName},'%')



    AND status=#{status}



    AND create_time  >=  #{startTime}


    AND create_time  <=  #{endTime}



    AND authentication_time  >=  #{startTime1}



    AND authentication_time  <=  #{endTime1}



    AND checked_time  >=  #{startTime2}


    AND checked_time  <=  #{endTime2}

ORDER BY
create_time DESC
LIMIT #{offset},#{pageSize}

mapper.update:
UPDATE tb_merchant_info
SET bank_pic=#{photoName}
WHERE id = #{merchantId}

UPDATE tb_merchant_info
SET bank_hand_pic=#{photoName}
WHERE id = #{merchantId}

UPDATE tb_merchant_info
SET identity_hand_pic=#{photoName}
WHERE id = #{merchantId}

UPDATE tb_merchant_info
SET identity_face_pic=#{photoName}
WHERE id = #{merchantId}

UPDATE tb_merchant_info
SET identity_opposite_pic=#{photoName}
WHERE id = #{merchantId}
