mapper.select:
SELECT COUNT(1) FROM tb_order_record WHERE merchant_id = #{merchantId} AND status=0

	AND create_time  >=  #{startDate}


	AND create_time  <=  #{endDate}


	AND order_id = #{orderId}

SELECT  FROM tb_order_record
WHERE merchant_id = #{merchantId} AND status=0

	AND create_time  >=  #{startDate}


	AND create_time  <=  #{endDate}


	AND order_id = #{orderId}

ORDER BY create_time DESC limit #{offset}, #{size}

SELECT  FROM tb_order_record
WHERE merchant_id = #{merchantId} AND status=0 and pay_result='S' and trade_type=0 and merchant_type=0
ORDER BY create_time DESC limit #{offset}, #{size}

SELECT o.update_time,o.order_id,o.merchant_id,o.sub_name,o.total_fee,o.pay_result,o.pay_channel,bank_no,mobile
FROM tb_order_record o
LEFT JOIN tb_merchant_info m
ON o.merchant_id = m.id
WHERE 1 = 1

	AND o.update_time >#{startTime}


	AND o.update_time < #{endTime}


	AND o.order_id = #{orderId}


	AND o.merchant_id = #{merchantId}


	AND o.sub_name = #{subName}


	AND o.total_fee > #{lessTotalFee}


	AND o.total_fee < #{moreTotalFee}


	AND o.pay_result = #{payResult}


	AND o.pay_channel = #{payChannel}


	AND md_mobile = #{mdMobile}

ORDER BY o.update_time DESC

	LIMIT ${pageNo},${pageSize}

		SELECT COUNT(0) FROM tb_order_record
        WHERE 1 = 1

			AND update_time > #{startTime}


			AND update_time < #{endTime}


			AND order_id = #{orderId}


			AND merchant_id = #{merchantId}


			AND sub_name = #{subName}


			AND total_fee > ${lessTotalFee}


			AND total_fee < ${moreTotalFee}


			AND pay_result = #{payResult}


			AND pay_channel = #{payChannel}

SELECT count(r.id)
FROM tb_order_record  r
LEFT JOIN tb_merchant_info m on r.merchant_id =m.id
WHERE   r.trade_type=1 and r.status= 0

	AND r.merchant_id =  #{merchantId}


	AND r.pay_result =  #{payResult}


	AND m.md_mobile =  #{mobile}


	AND m.bank_no_short =  #{bankNoShort}


	AND r.create_time  >=  #{startDate}


	AND r.create_time  <=  #{endDate}


	AND r.pay_time  >=  #{payStartDate}


	AND r.pay_time  <=  #{payEndDate}

SELECT r.id, r.order_id, r.merchant_id, r.merchant_type, r.product_name, r.pay_channel, r.total_fee, r.real_fee, r.service_fee, r.channel_fee, r.pay_result, r.out_trade_no, r.pay_params, r.result_params, r.error_message, r.body, r.pay_time, r.create_time, r.update_time, m.mobile, m.md_mobile, m.bank_no_short, m.reserve_mobile, m.merchant_name, m.address, m.name, m.identity, m.bank_name, m.bank_no FROM tb_order_record r LEFT JOIN tb_merchant_info m on r.merchant_id =m.id WHERE r.trade_type=1 and r.status= 0
AND r.order_id = #{orderId}
AND r.merchant_id = #{merchantId}
AND r.pay_result = #{payResult}
AND m.name = #{name}
AND m.md_mobile = #{mobile}
AND m.bank_no_short = #{bankNoShort}
AND r.create_time >= #{startDate}
AND r.create_time <= #{endDate}
AND r.pay_time >= #{payStartDate}
AND r.pay_time <= #{payEndDate}
ORDER BY r.create_time DESC limit #{offset}, #{size}
