mapper.insert:
INSERT INTO tb_push_message_record_new (title, content, temp_type, push_type, targets, transaction_number, voice)
SELECT #{title}, #{content}, #{tempType}, 2, #{targets}, #{transactionNumber}, #{voice} FROM DUAL
WHERE NOT EXISTS (SELECT 1 FROM tb_push_message_record_new WHERE transaction_number = #{transactionNumber} AND voice = #{voice});

mapper.update:
UPDATE tb_push_message_record_new
SET
task_id = #{taskId},
push_type = #{pushType},
status = #{status}
WHERE id = #{id}
