mapper.select:
SELECT COUNT(0) FROM tb_settle_exception_record


        AND settle_target_no = #{userNo}


        AND settle_target_name like "%"#{userName}"%"


        AND settle_target_type = #{userType}


        AND status = #{settleExceptionStatus}


        AND settle_date BETWEEN #{startDate} AND #{endDate}

SELECT * FROM tb_settle_exception_record


        AND settle_target_no = #{userNo}


        AND settle_target_name like "%"#{userName}"%"


        AND settle_target_type = #{userType}


        AND status = #{settleExceptionStatus}


        AND settle_date BETWEEN #{startDate} AND #{endDate}


ORDER BY id DESC
LIMIT #{offset}, #{count}
